$(function(){$(".metro-tile").bind("mousedown",function(a){var b=33;var c=16;var d=72;var e=66;var f=a.pageX-this.offsetLeft-$(this).parent().offset().left;var g=a.pageY-this.offsetTop-$(this).parent().offset().top;var h=$(this).width();var i=$(this).height();var j=parseInt(f/h*100);var k=parseInt(g/i*100);var l=j<=b&&k>=c&&k<d;var m=j>=e&&k>c&&k<d;var n=k>0&&k<=c;var o=k>d;var p=k>c&&k<d&&j>b&&j<e;if(m)$(this).addClass("tile_right");else if(l)$(this).addClass("tile_left");else if(o)$(this).addClass("tile_bottom");else if(n)$(this).addClass("tile_top");else if(p)$(this).addClass("tile_middle");console.log("x:"+f+", y:"+g+", h:"+i+", w:"+h+", wp"+j+", hp"+k);console.log("left:"+l);console.log("right:"+m);console.log("top:"+n);console.log("bottom:"+o);console.log("middle:"+p);return false})});$(function(){$(".metro-surface").mouseup(function(a){$(".metro-tile").removeClass("tile_left");$(".metro-tile").removeClass("tile_right");$(".metro-tile").removeClass("tile_bottom");$(".metro-tile").removeClass("tile_top");$(".metro-tile").removeClass("tile_middle");$(".metro-tile").addClass("tile_reset");return false})})